import { useState } from 'react';
import './filter.scss';
import useFilter from './usefilter';

const Filter = () => {
    const [term, setTerm] = useState("");
    //const [todos] = useFilter("https://jsonplaceholder.typicode.com/todos")
    const [todos] = useFilter("https://flight-a262e-default-rtdb.firebaseio.com/")
    // const [todos] = useFilter("https://partner.imwallet.in/web_services/statudentFilter.jsp")
  // const list = todos
  //   .slice(0, 10)
  //   .filter(({ title }) => title.search(term)>=0 );

  return (
    <div>
      <h1>Filter Todos</h1>
      <input
        type="text"
        value={term}
        onChange={(e) => setTerm(e.target.value)}
        placeholder="search"
      />

      {todos.map((user) => (
        <div key={user.id}>
          <p>
            <b>{user.title}</b>
          </p>
        </div>
      ))}
    </div>
  );
};
export default Filter;
